import axios from 'axios';
import { useEffect, useState } from 'react';

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [properties, setProperties] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersResponse = await axios.get('http://localhost:5000/api/auth/users', {
          headers: { Authorization: `Bearer ${user.token}` },
        });
        const propertiesResponse = await axios.get('http://localhost:5000/api/properties/admin', {
          headers: { Authorization: `Bearer ${user.token}` },
        });
        setUsers(usersResponse.data);
        setProperties(propertiesResponse.data);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };
    if (user && user.role === 'admin') fetchData();
  }, []);

  const handleDeleteProperty = async (propertyId) => {
    try {
      await axios.delete(`http://localhost:5000/api/properties/${propertyId}`, {
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setProperties(properties.filter((p) => p._id !== propertyId));
    } catch (err) {
      console.error('Error deleting property:', err);
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div>
          <h2 className="text-2xl font-semibold mb-4">Users</h2>
          <ul className="space-y-4">
            {users.map((u) => (
              <li key={u._id} className="bg-white shadow p-4 rounded-lg">
                <p className="text-sm text-gray-700">Name: {u.name}</p>
                <p className="text-sm text-gray-700">Email: {u.email}</p>
                <p className="text-sm text-gray-700">Role: {u.role}</p>
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h2 className="text-2xl font-semibold mb-4">Properties</h2>
          <ul className="space-y-4">
            {properties.map((p) => (
              <li key={p._id} className="bg-white shadow p-4 rounded-lg flex justify-between">
                <div>
                  <p className="text-sm text-gray-700">Title: {p.title}</p>
                  <p className="text-sm text-gray-700">Location: {p.location}</p>
                  <p className="text-sm text-gray-700">Price: â‚¹{p.price.toLocaleString()}</p>
                </div>
                <button
                  onClick={() => handleDeleteProperty(p._id)}
                  className="text-red-600 hover:text-red-800"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
